function openChat() {
  $(".chat-suporte").hasClass("ativo-chat-suporte") ? $(".chat-suporte").removeClass("ativo-chat-suporte") : ($(".chat-suporte").addClass("ativo-chat-suporte"),
  sessionStorage.setItem("chat_online", 1),
  $(".icon-chat").removeClass("mostra-msg-chat"))
}
$(function() {
  $(".fechar_chat").on("click", function() {
      $(".chat-suporte").removeClass("ativo-chat-suporte")
  }),
  $(".fechar_msg_chat").on("click", function() {
      sessionStorage.setItem("chat_online", 1),
      $(".icon-chat").removeClass("mostra-msg-chat")
  }),
  sessionStorage.getItem("chat_online") || $(".icon-chat").addClass("mostra-msg-chat")
});
